import java.util.Arrays;


public class Sortarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	
		   int i[] = {6,5,4,3,2,1};

		   for (int number : i) {
		   System.out.println("Number = " + number);
		   }

		   Arrays.sort(i);

		 
		   System.out.println("The sorted int array is:");
		   for (int number : i) {
		   System.out.println("Number = " + number);
	}

}
}

